			$criteria->addSelectColumn(PurchaseOrderItemPeer::COUNT_DISTINCT);
		} else {
			$criteria->addSelectColumn(PurchaseOrderItemPeer::COUNT);
		}

		// just in case we're grouping: add those columns to the select statement
		foreach($criteria->getGroupByColumns() as $column)
		{
			$criteria->addSelectColumn($column);
		}

		$criteria->addJoin(PurchaseOrderItemPeer::PRODUCTID, ProductPeer::ID);

		$rs = PurchaseOrderItemPeer::doSelectRS($criteria, $con);
		if ($rs->next()) {
			return $rs->getInt(1);
		} else {
			// no rows returned; we infer that means 0 matches.
			return 0;
		}
	}


	/**
	 * Selects a collection of PurchaseOrderItem objects pre-filled with all related objects except Product.
	 *
	 * @return     array Array of PurchaseOrderItem objects.
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function doSelectJoinAllExceptProduct(Criteria $c, $con = null)
	{
		$c = clone $c;

		// Set the correct dbName if it has not been overridden
		// $c->getDbName() will return the same object if not set to another value
		// so == check is okay and faster
		if ($c->getDbName() == Propel::getDefaultDB()) {
			$c->setDbName(self::DATABASE_NAME);
		}

		PurchaseOrderItemPeer::addSelectColumns($c);
		$startcol2 = (PurchaseOrderItemPeer::NUM_COLUMNS - PurchaseOrderItemPeer::NUM_LAZY_LOAD_COLUMNS) + 1;

		SupplierPurchaseOrderPeer::addSelectColumns($c);
		$startcol3 = $startcol2 + SupplierPurchaseOrderPeer::NUM_COLUMNS;

		$c->addJoin(PurchaseOrderItemPeer::SUPPLIERPURCHASEORDERID, SupplierPurchaseOrderPeer::ID);


		$rs = BasePeer::doSelect($c, $con);
		$results = array();

		while($rs->next()) {

			$omClass = PurchaseOrderItemPeer::getOMClass();

			$cls = Propel::import($omClass);
			$obj1 = new $cls();
			$obj1->hydrate($rs);

			$omClass = SupplierPurchaseOrderPeer::getOMClass();


			$cls = Propel::import($omClass);
			$obj2  = new $cls();
			$obj2->hydrate($rs, $startcol2);

			$newObject = true;
			for ($j=0, $resCount=count($results); $j < $resCount; $j++) {
				$temp_obj1 = $results[$j];
				$temp_obj2 = $temp_obj1->getSupplierPurchaseOrder(); //CHECKME
				if ($temp_obj2->getPrimaryKey() === $obj2->getPrimaryKey()) {
					$newObject = false;
					$temp_obj2->addPurchaseOrderItem($obj1);
					break;
				}
			}

			if ($newObject) {
				$obj2->initPurchaseOrderItems();
				$obj2->addPurchaseOrderItem($obj1);
			}

			$results[] = $obj1;
		}
		return $results;
	}


