		if ($cols) {
			$dbMap = Propel::getDatabaseMap(PurchaseOrderItemPeer::DATABASE_NAME);
			$tableMap = $dbMap->getTable(PurchaseOrderItemPeer::TABLE_NAME);

			if (! is_array($cols)) {
				$cols = array($cols);
