CREATE TABLE IF NOT EXISTS `users_user` ( `id` int(11) NOT NULL auto_increment COMMENT 'User Id', `username` varchar(255) collate latin1_general_cs NOT NULL COMMENT 'username', `password` varchar(255) collate latin1_general_cs NOT NULL COMMENT 'password', `active` int(11) NOT NULL COMMENT 'Is user active?', `created` datetime NOT NULL COMMENT 'Creation date for', `updated` datetime NOT NULL COMMENT 'Last update date', `levelId` int(11) default NULL COMMENT 'User Level', `lastLogin` datetime default NULL COMMENT 'Fecha del ultimo login del usuario', PRIMARY KEY (`id`), UNIQUE KEY `users_user_U_1` (`username`), KEY `users_user_ibfk_2` (`levelId`) ) ENGINE=MyISAM DEFAULT CHARSET=latin1 COMMENT='Users';
